  private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
    // Datos
    int pp = Integer.parseInt(txtPromPract.getText());
    int ep = Integer.parseInt(txtExaParcial.getText());
    int ef = Integer.parseInt(txtExaFinal.getText());
    // Proceso
    int pf = PromedioService.calcPromedio(pp, ep, ef);
    String cond = PromedioService.obtenerCondicion(pf);
    // Reporte
    txtPromFinal.setText(String.valueOf(pf));
    txtCondicion.setText(cond);
  }//GEN-LAST:event_jButton1ActionPerformed

